/**
 * Exceção a ser lançada quando ocorrer uma condição que altera o fluxo normal da execução do programa no contexto de regra de negócio.
 * @author Luiz Henrique
 */
public class RNException extends Exception {

    private static final long serialVersionUID = -936364196317968175L;

    public RNException() {
        // TODO Auto-generated constructor stub
    }

    public RNException(String message) {
        super(message);
        // TODO Auto-generated constructor stub
    }

    public RNException(Throwable cause) {
        super(cause);
        // TODO Auto-generated constructor stub
    }

    public RNException(String message, Throwable cause) {
        super(message, cause);
        // TODO Auto-generated constructor stub
    }

    public RNException(String message, Throwable cause,
            boolean enableSuppression, boolean writableStackTrace) {
        super(message, cause, enableSuppression, writableStackTrace);
        // TODO Auto-generated constructor stub
    }
}
